import React, { Component } from "react";
import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

class Episodes extends Component {
  constructor(props) {
    super(props);
    this.state = { episode: "" };
  }

  componentDidMount() {
    spotifyWebApi.getEpisode("512ojhOuo1ktJprKbVcKyQ").then((response) => {
      console.log(response);
      this.setState({
        episode: response,
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <p>PODCAST EPISODE</p>
        <img
          src="https://i.scdn.co/image/3c59a8b611000c8b10c8013013c3783dfb87a3bc"
          alt="episode thumbnail"
        />

        <div className="container">{this.state.episode.name}</div>
        <p>{this.state.episode.description}</p>
      </React.Fragment>
    );
  }
}

export default Episodes;
