import React from "react";
import axios from "axios";

export default class SongList extends React.Component {
  state = {
    myTokenId: "f0ZBfcDiL9WPYeCR",
    myUserId: "7926",
    songs: [],
    searchInput: "",
  };

  handleChange = (event) => {
    this.setState({ searchInput: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    axios
      .get(
        `https://www.abbreviations.com/services/v2/lyrics.php?uid=${this.state.myUserId}&tokenid=${this.state.myTokenId}&term=${this.state.searchInput}&format=json`
      )
      .then((res) => {
        this.setState({ songs: res.data.result });
      });
  };

  render() {
    return (
      <React.Fragment>
        <form onSubmit={this.handleSubmit}>
          <label>
            Search lyrics:
            <input type="text" name="name" onChange={this.handleChange} />
          </label>
          <button type="submit">Add</button>
        </form>
        <ul>
          {this.state.songs.slice(0, 10).map((result) => (
            <li>{result.album}</li>
          ))}

          {/* {this.state.persons.map((person) => (
          <li key={person.id}>{person.name}</li>
        ))} */}
        </ul>
      </React.Fragment>
    );
  }
}
