import React, { Component } from "react";
import EpisodeSidebar from "./EpisodeSidebar";
import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

class Episode extends Component {
  constructor(props) {
    super(props);
    this.state = { episode: "" };
  }

  componentDidMount() {
    spotifyWebApi.getEpisode("512ojhOuo1ktJprKbVcKyQ").then((response) => {
      console.log(response);
      this.setState({
        episode: response,
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <div className="container">
          <div className="row">
            <div className="col col-lg-8">
              <p>PODCAST EPISODE</p>
              <img
                src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
                alt="episode thumbnail"
              />

              <div className="container">{this.state.episode.name}</div>
              <h2>Episode description</h2>
              <p>{this.state.episode.description}</p>
            </div>
          </div>
        </div>

        <EpisodeSidebar />
      </React.Fragment>
    );
  }
}

export default Episode;
