import React, { Component } from "react";
import { faComments } from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class LandingPage extends Component {
  checkAuthentication() {
    if (this.props.auth) {
      window.history.pushState({}, "", "/discover");
    } else {
      return (
        <div>
          <div className="LandingPageContainer">
            <div className="row">
              <div className="col-lg-6 col-sm-12">
                <h3 className="episodeType">Ramp up challenge 2020</h3>
                <h1 className="episodeTitle">Introducing Spotify Topics </h1>
                <p>
                  Spotify topics offer a timestamp navigation system for
                  podcasts. The goal is to help you navigate through the content
                  of the episode and to easier find interesting topics discussed
                  in different podcasts in order to discover new podcasts.
                </p>
                <br /> <br />
                <a
                  href="http://localhost:8888/login"
                  className="openInSpotifyBtn"
                >
                  Log in with Spotify
                </a>
              </div>
              <div className="col-lg-6 col-sm-12">Bild här sen..</div>
            </div>
          </div>
          <div className="footer">
            Made by{" "}
            <a href="https://www.linkedin.com/in/johan-akerman/">
              Johan Åkerman
            </a>
          </div>
        </div>
      );
    }
  }

  render() {
    return <div>{this.checkAuthentication()}</div>;
  }
}

export default LandingPage;
