import React, { Component } from "react";
import "./App.css";
import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify(); //needs to be initialized since it is a class.

class App extends Component {
  constructor() {
    super();
    const params = this.getHashParams();
    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: "not checked",
      },
      songs: [],
    };

    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  getNowPlaying() {
    spotifyWebApi.getMyCurrentPlaybackState().then((response) => {
      this.setState({
        nowPlaying: {
          name: response.item.name,
        },
      });
    });
  }

  searchSong() {
    spotifyWebApi.searchTracks("hello", "track").then((response) => {
      this.setState({
        // songs: response.tracks.items,
        songs: response.tracks.items.sort(
          (a, b) => parseFloat(b.popularity) - parseFloat(a.popularity) //sorts out of popularity
        ),
      });

      console.log(this.state.songs);
    });
  }

  showSignIn() {
    if (!this.state.loggedIn) {
      return (
        <a href="http://localhost:8888">
          <button>Log in with Spotify</button>
        </a>
      );
    } else {
      return <div>Logged in</div>;
    }
  }

  render() {
    return (
      <div className="App">
        {this.showSignIn()}
        <div>Now playing: {this.state.nowPlaying.name}</div>
        <button onClick={() => this.getNowPlaying()}>Check now playing</button>
        <button onClick={() => this.searchSong()}>search</button>

        <table className="myTable">
          <thead>
            <tr>
              <th>Song name</th>
              <th>Authors</th>
              <th>Popularity</th>
            </tr>
          </thead>
          <tbody>
            {this.state.songs.map((result) => (
              <tr key={result.id}>
                <td>{result.name}</td>
                <td>{result.artists.map((result) => "," + result.name)}</td>
                <td>{result.popularity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default App;
