import React, { Component } from "react";
import ListGroup from "react-bootstrap/ListGroup";

import "../App.css";
import Spotify from "spotify-web-api-js";
import { Link } from "react-router-dom";
const spotifyWebApi = new Spotify();

class Episode extends Component {
  constructor(props) {
    super(props);
    this.state = { episode: "" };
  }

  componentDidMount() {
    spotifyWebApi.getEpisode("512ojhOuo1ktJprKbVcKyQ").then((response) => {
      console.log(response);
      this.setState({
        episode: response,
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <div className="">
          <div className="row">
            <div className="col col-lg-8 episodeDescriptionContainer">
              <div className="episodeDescriptionInnerContainer">
                <Link className="backBtn" to="/">
                  ← Back to episodes
                </Link>
                <div className="row episodeHeaderRow">
                  <div class="col-lg-3">
                    <img
                      className="episode-thumbnail"
                      src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
                      alt="episode thumbnail"
                    />
                  </div>

                  <div className="col-lg-9 episodeHeader">
                    <div className="episodeFragment">
                      <h3 className="episodeType">Podcast episode</h3>
                      <h1 className="episodeTitle">lkjasdfsadf</h1>
                      <h2 className="episodePublisher">Publisher</h2>
                    </div>
                  </div>
                </div>

                <div className="episodePlayer">Player will be here</div>

                <div className="episodeDescriptionContainer">
                  <h2 className="episodeDescriptionTitle">
                    Episode description
                  </h2>
                  <p className="episodeDescriptionText">
                    {this.state.episode.description}
                  </p>
                </div>
              </div>
            </div>

            <div className="col col-lg-4 episodeTopicsContainer">
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <span className="listGroupItemTitle">title</span>
                  <span className="listGroupItemTime">Time</span>
                </ListGroup.Item>

                <ListGroup.Item className="activeListGroup">
                  <span className="listGroupItemTitle">title</span>
                  <span className="listGroupItemTime">Time</span>
                </ListGroup.Item>

                <ListGroup.Item>
                  <span className="listGroupItemTitle">title</span>
                  <span className="listGroupItemTime">Time</span>
                </ListGroup.Item>

                <ListGroup.Item>
                  <span className="listGroupItemTitle">title</span>
                  <span className="listGroupItemTime">Time</span>
                </ListGroup.Item>
              </ListGroup>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Episode;
