import React, { Component } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import Spotify from "spotify-web-api-js";

const trendingTopics = [
  {
    title: "Spotify's framtid",
    publisher: "#ensakidag - en riktigt viktig sak varje morgon",
    url: "2QG2XPrjKY6HYSjmxflMzP",
    start: 0,
    stop: 7,
    key: 1,
  },
  {
    title: "Framtidens självkörande lastbilar",
    publisher: "Under Femton",
    url: "2R4VEgJfLXD2wKmL9c11l6",
    start: 0,
    stop: 7,
    key: 2,
  },
  {
    title: "Europas största batteri fabrik",
    publisher: "Allt du behöver veta om ny teknik",
    url: "7LWWaN9qyuSMEg2I0DLG6z",
    start: 0,
    stop: 7,
    key: 3,
  },
  {
    title: "How to get an internship at Spotify",
    publisher: "The Greenroom",
    url: "6oyFNKq3atN7U9splhDnuI",
    start: 0,
    stop: 7,
    key: 4,
  },
  {
    title: "How to think big",
    publisher: "The Tim Ferris Show",
    url: "3C3imZeq18TABmcswlHgzY",
    start: 0,
    stop: 7,
    key: 5,
  },
  {
    title: "Livet på KTH",
    publisher: "Mitt liv som ingenjör",
    url: "2K25jghHW8RNdDbUf2dLde",
    start: 0,
    stop: 7,
    key: 6,
  },
  {
    title: "Hur man bygger ett imperium",
    publisher: "Framgångspodden",
    url: "4OWSOjrnNGMp6hn4NYVqBl",
    start: 0,
    stop: 7,
    key: 7,
  },
];

const spotifyWebApi = new Spotify();

class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = { episodes: [], user: "" };
  }

  componentDidMount() {
    spotifyWebApi
      .getEpisodes([
        "0QVuQXFPUitPeXH727JLgH",
        "2QG2XPrjKY6HYSjmxflMzP",
        "2R4VEgJfLXD2wKmL9c11l6",
        "7LWWaN9qyuSMEg2I0DLG6z",
        "3C3imZeq18TABmcswlHgzY",
        "6oyFNKq3atN7U9splhDnuI",
        "2K25jghHW8RNdDbUf2dLde",
        "4OWSOjrnNGMp6hn4NYVqBl",
      ])
      .then((response) => {
        this.setState({
          episodes: response.episodes,
        });
      });

    spotifyWebApi.getMe().then((response) => {
      this.setState({
        user: response,
      });
    });
  }

  render() {
    return (
      <div className="episodesContainer">
        <h1>Welcome {this.state.user.display_name},</h1>
        <br />

        <div className="row">
          <div className="col-lg-8">
            <h2>Recommended episodes</h2>

            <div className="row">
              {this.state.episodes &&
                this.state.episodes.map((episode) => {
                  return (
                    <div className="col-lg-3" key={`episode - ${episode.id}`}>
                      <Link
                        className="podcastEpisodeCardLink"
                        to={`/discover/${episode.id}`}
                      >
                        <div className="podcastEpisodeCard">
                          <img
                            alt="thumbnail"
                            className="img img-fluid"
                            src={episode.images[0].url}
                          />
                          <h1 className="hideToLongText">{episode.name}</h1>
                          <p className="hideToLongText">{episode.show.name}</p>
                        </div>
                      </Link>
                    </div>
                  );
                })}
            </div>
          </div>

          <div className="col-lg-4">
            <h2>Trending topics</h2>

            {trendingTopics.map((topic) => {
              return (
                <Link
                  className="podcastEpisodeCardLink"
                  to={{
                    pathname: `/discover/${topic.url}`,
                    state: {
                      startTime: topic.url,
                    },
                  }}
                >
                  <span className="badge badge-secondary badgeTopic">
                    <h1>{topic.title}</h1>
                    <p>{topic.publisher}</p>
                  </span>
                </Link>
              );
            })}
          </div>
        </div>

        <br />
      </div>
    );
  }
}

export default Discover;
