import React, { Component, useState, useEffect, useCallback } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import Spotify from "spotify-web-api-js";

const spotifyWebApi = new Spotify();

class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = { episodes: [], user: "" };
  }

  componentDidMount() {
    spotifyWebApi
      .getEpisodes(["77o6BIVlYM3msb4MMIL1jH", "0Q86acNRm6V9GYx55SXKwf"])
      .then((response) => {
        this.setState({
          episodes: response.episodes,
        });
      });

    spotifyWebApi.getMe().then((response) => {
      this.setState({
        user: response,
      });
    });
  }

  render() {
    return (
      <div className="episodesContainer">
        {this.state.user && this.state.user.display_name && (
          <h1>Welcome {this.state.user.display_name},</h1>
        )}
        <h1>Welcome {this.state.user.display_name},</h1>
        <br />
        {/* <h1 key={`episode - ${episode.id}`}>{episode.name}</h1> */}
        <h2>Recommended episodes</h2>
        {console.log(this.state.episodes)}

        <div className="row">
          {this.state.episodes &&
            this.state.episodes.map((episode) => {
              return (
                <div className="col-lg-2" key={`episode - ${episode.id}`}>
                  <Link to="/episode">
                    <div className="podcastEpisodeCard">
                      <img
                        alt="thumbnail"
                        className="img img-fluid"
                        src={episode.images[2].url}
                      />
                      <h1>{episode.name}</h1>
                      <p>{episode.show.name}</p>
                    </div>
                  </Link>
                </div>
              );
            })}
        </div>

        <br />

        <h2>Trending topics</h2>
        <a>
          <span className="badge  badge-secondary badgeTopic">
            <h1>Introduction to marketing</h1>
            <p>Gary Vaynerchuk</p>
          </span>
        </a>
      </div>
    );
  }
}

export default Discover;
