import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
} from "react-router-dom";

import Table from "react-bootstrap/Table";
import "./App.css";
import Spotify from "spotify-web-api-js";
import Header from "./components/Header";
const spotifyWebApi = new Spotify(); //needs to be initialized since it is a class.
function Home() {
  return <h2>Home</h2>;
}

function About() {
  return <h2>About</h2>;
}

function Users() {
  return <h2>Users</h2>;
}
class App extends Component {
  constructor() {
    super();
    const params = this.getHashParams();
    this.state = {
      loggedIn: params.access_token ? true : false,
      nowPlaying: {
        name: "not checked",
      },
      songs: [],
      searchInput: "",
    };

    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  handleChange = (event) => {
    this.setState({ searchInput: event.target.value });
  };

  searchSong = (event) => {
    event.preventDefault();
    spotifyWebApi
      .searchEpisodes(this.state.searchInput, "episode")
      .then((response) => {
        console.log(response);
        this.setState({
          songs: response.episodes.items,
          // songs: response.tracks.items,
          // songs: response.episodes.items.sort(
          //   (a, b) => parseFloat(b.popularity) - parseFloat(a.popularity) //sorts out of popularity
          // ),
        });

        console.log(this.state.songs);
      });
  };

  showSignIn() {
    if (!this.state.loggedIn) {
      return (
        <a href="http://localhost:8888">
          <button>Log in with Spotify</button>
        </a>
      );
    } else {
      return <div>Logged in</div>;
    }
  }

  renderTable() {
    if (this.state.songs === "") {
      return <div>No songs...</div>;
    } else {
      return (
        <Table className="myTable">
          <thead>
            <tr>
              <th>Title</th>
              <th>Artist</th>
              <th>Popularity</th>
            </tr>
          </thead>
          <tbody>
            {this.state.songs.map((result) => (
              <tr key={result.id}>
                <td>{result.name}</td>
                <td>{result.release_date}</td>
                <td>{result.duration_ms}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      );
    }
  }

  render() {
    return (
      <React.Fragment>
        <Router>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/topics">Topics</Link>
              </li>
            </ul>

            <Switch>
              <Route path="/about">
                <About />
              </Route>
              <Route path="/topics">
                <Topics />
              </Route>
              <Route path="/">
                <Home />
              </Route>
            </Switch>
          </div>
        </Router>
        <Header />
        {this.showSignIn()}
        <div className="container">
          <form onSubmit={this.searchSong}>
            <label>
              <h1>Search for a song</h1>
              <br></br>
              <input
                type="text"
                name="name"
                onChange={this.handleChange}
                autoComplete="off"
              />
            </label>
            <button type="submit">Search lyrics</button>
          </form>

          {this.renderTable()}
        </div>
      </React.Fragment>
    );
  }
}

export default App;
