import React, { Component } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import Carousel from "react-bootstrap/Carousel";
import Spotify from "spotify-web-api-js";

const spotifyWebApi = new Spotify();

class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = { episode: "", user: "" };
  }

  componentDidMount() {
    spotifyWebApi.getEpisode("512ojhOuo1ktJprKbVcKyQ").then((response) => {
      console.log(response);
      this.setState({
        episode: response,
      });
    });

    spotifyWebApi.getMe().then((response) => {
      console.log(response);
      this.setState({
        user: response,
      });
    });
  }

  render() {
    return (
      <div className="episodesContainer">
        <h1>Welcome {this.state.user.display_name},</h1>
        <br />
        <br />

        <Carousel>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="https://i.ytimg.com/vi/szs1YwKVME4/maxresdefault.jpg"
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>First slide label</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=Second slide&bg=282c34"
              alt="Third slide"
            />

            <Carousel.Caption>
              <h3>Second slide label</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=Third slide&bg=20232a"
              alt="Third slide"
            />

            <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        <h2>Recommended episodes</h2>
        <div className="row">
          <div class="col-lg-2">
            <Link to="/episode">
              <div class="podcastEpisodeCard">
                <img
                  alt="thumbnail"
                  className="img img-fluid"
                  src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
                />
                <h1>Skitsnack</h1>
                <p>Anis don Demina</p>
              </div>
            </Link>
          </div>
        </div>
        <br />
        <br />
        <br />

        {/* <h2>Trending topics</h2>
        <a>
          <span class="badge  badge-secondary badgeTopic">
            <h1>Introduction to marketing</h1>
            <p>Anis don Demina</p>
          </span>
        </a>

        <br />
        <br />
        <br />

        <h2>Topics you may find interesting</h2>
        <a>
          <span class="badge  badge-secondary badgeTopic">
            <h1>Introduction to marketing</h1>
            <p>Anis don Demina</p>
          </span>
        </a>
        <a>
          <span class="badge  badge-secondary badgeTopic">
            <h1>Traditional marketing</h1>
            <p>Anis don Demina</p>
          </span>
        </a> */}
      </div>
    );
  }
}

export default Discover;
