import React, { Component } from "react";
import ListGroup from "react-bootstrap/ListGroup";

import "../App.css";
import Spotify from "spotify-web-api-js";
import { Link } from "react-router-dom";
const spotifyWebApi = new Spotify();

class Episode extends Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();
    this.state = { loggedIn: params.access_token ? true : false, episode: "" };
    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  componentDidMount() {
    spotifyWebApi.getEpisode("512ojhOuo1ktJprKbVcKyQ").then((response) => {
      console.log(response);
      this.setState({
        episode: response,
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <div className="">
          <div className="row">
            <div className="col col-lg-8 episodeDescriptionContainer">
              <div className="episodeDescriptionInnerContainer">
                <Link className="backBtn" to="/episodes">
                  ← Back to episodes
                </Link>
                <div className="row episodeHeaderRow">
                  <div class="col-lg-3">
                    <img
                      className="episode-thumbnail"
                      src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
                      alt="episode thumbnail"
                    />
                  </div>

                  <div className="col-lg-9 episodeHeader">
                    <div className="episodeFragment">
                      <h3 className="episodeType">Podcast episode</h3>
                      <h1 className="episodeTitle">
                        {" "}
                        {this.state.episode.name}
                      </h1>
                      <h2 className="episodePublisher">Showname</h2>
                    </div>
                  </div>
                </div>

                <div className="episodePlayer">Player will be here</div>

                <div className="episodeDescriptionContainer">
                  <h2 className="episodeDescriptionTitle">
                    Episode description
                  </h2>
                  <p className="episodeDescriptionText">
                    {this.state.episode.description}
                  </p>
                </div>
              </div>
            </div>

            <div className="col col-lg-4 episodeTopicsContainer">
              <ListGroup variant="flush">
                <ListGroup.Item className="listGroupItemHeader">
                  Episode topics
                </ListGroup.Item>

                <ListGroup.Item>
                  <span className="listGroupItemTitle">
                    Introduction to marketing
                  </span>
                  <span className="listGroupItemTime">00:00 - 02:43</span>
                </ListGroup.Item>

                <ListGroup.Item className="activeListGroup">
                  <span className="listGroupItemTitle">
                    Traditional marketing
                  </span>
                  <span className="listGroupItemTime">02:43 - 05:26</span>
                </ListGroup.Item>

                <ListGroup.Item>
                  <span className="listGroupItemTitle">Email marketing</span>
                  <span className="listGroupItemTime">05:26 - 13:29</span>
                </ListGroup.Item>

                <ListGroup.Item>
                  <span className="listGroupItemTitle">
                    The future of marketing
                  </span>
                  <span className="listGroupItemTime">13:29 - 24:32</span>
                </ListGroup.Item>
              </ListGroup>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Episode;
