import React from "react";
import axios from "axios";
const myTokenId = "f0ZBfcDiL9WPYeCR";
const myUserId = "7926";
//.get(`https://jsonplaceholder.typicode.com/users?_limit=10`)

export default class SongList extends React.Component {
  state = {
    myTokenId: "f0ZBfcDiL9WPYeCR",
    myUserId: "7926",
    persons: [],
    songs: [],
  };

  componentDidMount() {
    axios
      .get(
        `https://www.abbreviations.com/services/v2/lyrics.php?uid=${this.state.myUserId}&tokenid=${this.state.myTokenId}&term=forever%20young&format=json`
      )
      .then((res) => {
        console.log(res);
        this.setState({ songs: res.data.result });
      });
  }
  // const persons = res.data;
  // this.setState({ persons });

  //);

  render() {
    return (
      <ul>
        {this.state.songs.slice(0, 10).map((result) => (
          <li>{result.album}</li>
        ))}

        {/* {this.state.persons.map((person) => (
          <li key={person.id}>{person.name}</li>
        ))} */}
      </ul>
    );
  }
}
