import React, { Component } from "react";
import "../App.css";

import Spotify from "spotify-web-api-js";

const spotifyWebApi = new Spotify();

class Episodes extends Component {
  constructor(props) {
    super(props);
    this.state = { episode: "" };
  }

  componentDidMount() {
    spotifyWebApi.getEpisode("512ojhOuo1ktJprKbVcKyQ").then((response) => {
      console.log(response);
      this.setState({
        episode: response,
      });
    });
  }

  render() {
    return (
      <div className="episodesContainer">
        <div className="row">
          <div class="col-lg-2">
            <div class="podcastEpisodeCard">
              <img
                className="img img-fluid"
                src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
              />
              <h1>{this.state.episode.name}</h1>
              <p>Podcast episode description will go here</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Episodes;
