import React, { Component, useState, useEffect, useCallback } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import Spotify from "spotify-web-api-js";

const spotifyWebApi = new Spotify();

const Discover = ({ test, test2, test3, children }) => {
  const [episodes, setEpisodes] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    spotifyWebApi
      .getEpisodes(["77o6BIVlYM3msb4MMIL1jH", "0Q86acNRm6V9GYx55SXKwf"])
      .then((response) => {
        setEpisodes(response);
      });

    spotifyWebApi.getMe().then((response) => {
      setUser(response);
    });
  }, []);

  console.log(episodes);

  return (
    <div className="episodesContainer">
      {user && user.display_name && <h1>Welcome {user.display_name},</h1>}
      <br />

      <h2>Recommended episodes</h2>

      {episodes &&
        episodes.episodes.map((episode) => {
          return <h1>{episode.name}</h1>;
        })}
      <div className="row">
        <div class="col-lg-2">
          <Link to="/episode">
            <div class="podcastEpisodeCard">
              <img
                alt="thumbnail"
                className="img img-fluid"
                src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
              />
              <h1>Skitsnack</h1>
              <p>Anis don Demina</p>
            </div>
          </Link>
        </div>
      </div>

      <br />

      <h2>Trending topics</h2>
      <a>
        <span class="badge  badge-secondary badgeTopic">
          <h1>Introduction to marketing</h1>
          <p>Gary Vaynerchuk</p>
        </span>
      </a>
    </div>
  );
};

