import React, { Component } from "react";

class LandingPage extends Component {
  checkAuthentication() {
    if (this.props.auth) {
      window.history.pushState({}, "", "/discover");
    } else {
      return (
        <div style={{ textAlign: "center" }}>
          <h1>Spotify Topics</h1>
          Spotify topics offer a timestamp navigation system for podcasts. The
          goal is to help you navigate through the content of the episode and to
          easier find interesting topics discussed in different podcasts in
          order to discover new podcasts.
          <br /> <br />
          <a href="http://localhost:8888/login">
            <button>Log in with Spotify</button>
          </a>
        </div>
      );
    }
  }

  render() {
    return <div>{this.checkAuthentication()}</div>;
  }
}

export default LandingPage;
