import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
} from "react-router-dom";

import Table from "react-bootstrap/Table";
import "./App.css";
import Spotify from "spotify-web-api-js";
import Header from "./components/Header";
import Episodes from "./components/Episodes";
const spotifyWebApi = new Spotify(); //needs to be initialized since it is a class.
function Home() {
  return <h2>Home</h2>;
}

function About() {
  return <h2>About</h2>;
}

function Topics() {
  let match = useRouteMatch();

  return (
    <div>
      <h2>Topics</h2>
      <ul>
        <li>
          <Link to={`${match.url}/components`}>Components</Link>
        </li>
        <li>
          <Link to={`${match.url}/props-v-state`}>Props v. State</Link>
        </li>
      </ul>

      {/* The Topics page has its own <Switch> with more routes
          that build on the /topics URL path. You can think of the
          2nd <Route> here as an "index" page for all topics, or
          the page that is shown when no topic is selected */}
      <Switch>
        <Route path={`${match.path}/:topicId`}>
          <Topic />
        </Route>
        <Route path={match.path}>
          <h3>Please select a topic.</h3>
        </Route>
      </Switch>
    </div>
  );
}

function Topic() {
  let { topicId } = useParams();
  return <h3>Requested topic ID: {topicId}</h3>;
}
class App extends Component {
  constructor() {
    super();

    this.state = {
      nowPlaying: {
        name: "not checked",
      },
      songs: [],
      searchInput: "",
    };
  }

  handleChange = (event) => {
    this.setState({ searchInput: event.target.value });
  };

  searchSong = (event) => {
    event.preventDefault();
    spotifyWebApi
      .searchEpisodes(this.state.searchInput, "episode")
      .then((response) => {
        console.log(response);
        this.setState({
          songs: response.episodes.items,
          // songs: response.tracks.items,
          // songs: response.episodes.items.sort(
          //   (a, b) => parseFloat(b.popularity) - parseFloat(a.popularity) //sorts out of popularity
          // ),
        });

        console.log(this.state.songs);
      });
  };

  renderTable() {
    if (this.state.songs === "") {
      return <div>No songs...</div>;
    } else {
      return (
        <Table className="myTable">
          <thead>
            <tr>
              <th>Title</th>
              <th>Artist</th>
              <th>Popularity</th>
            </tr>
          </thead>
          <tbody>
            {this.state.songs.map((result) => (
              <tr key={result.id}>
                <td>{result.name}</td>
                <td>{result.release_date}</td>
                <td>{result.duration_ms}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      );
    }
  }

  render() {
    return (
      <React.Fragment>
        <Header />

        <Router>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/episodes">
                  <Episodes />
                </Link>
              </li>
              <li>
                <Link to="/topics">Topics</Link>
              </li>
            </ul>

            <Switch>
              <Route path="/about">
                <About />
              </Route>
              <Route path="/topics">
                <Topics />
              </Route>
              <Route path="/">
                <Home />
              </Route>
            </Switch>
          </div>
        </Router>

        <div className="container">
          <form onSubmit={this.searchSong}>
            <label>
              <h1>Search for a song</h1>
              <br></br>
              <input
                type="text"
                name="name"
                onChange={this.handleChange}
                autoComplete="off"
              />
            </label>
            <button type="submit">Search lyrics</button>
          </form>

          {this.renderTable()}
        </div>
      </React.Fragment>
    );
  }
}

export default App;
