class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = { episodes: [], user: "" };
  }

  componentDidMount() {
    spotifyWebApi
      .getEpisodes(["77o6BIVlYM3msb4MMIL1jH", "0Q86acNRm6V9GYx55SXKwf"])
      .then((response) => {
        this.setState({
          episodes: response.episodes,
        });
      });

    spotifyWebApi.getMe().then((response) => {
      this.setState({
        user: response,
      });
    });
  }

  render() {
    return (
      <div className="episodesContainer">
        {this.state.user && this.state.user.display_name && (
          <h1>Welcome {this.state.user.display_name},</h1>
        )}
        <h1>Welcome {this.state.user.display_name},</h1>
        <br />

        <h2>Recommended episodes</h2>
        {console.log(this.state.episodes)}
        {this.state.episodes &&
          this.state.episodes.map((episode) => {
            return (
              <h1 key={`episode - ${this.state.episode.id}`}>
                {this.state.episode.name}
              </h1>
            );
          })}
        <div className="row">
          <div className="col-lg-2">
            <Link to="/episode">
              <div className="podcastEpisodeCard">
                <img
                  alt="thumbnail"
                  className="img img-fluid"
                  src="https://i.scdn.co/image/6bcff849a483dd3c2883b3f0272848b909f1bbce"
                />
                <h1>Skitsnack</h1>
                <p>Anis don Demina</p>
              </div>
            </Link>
          </div>
        </div>

        <br />

        <h2>Trending topics</h2>
        <a>
          <span className="badge  badge-secondary badgeTopic">
            <h1>Introduction to marketing</h1>
            <p>Gary Vaynerchuk</p>
          </span>
        </a>
      </div>
    );
  }
}

