import React, { Component } from "react";
import Navbar from "react-bootstrap/Navbar";
import Logo from "../Spotify_Logo.png";

class Header extends Component {
  checkAuthentication() {
    if (this.props.auth) {
      return <div>Log out</div>;
    } else {
      return <div>Log in</div>;
    }
  }

  render() {
    if (!this.props.user.images) {
      return <span>Loading...</span>;
    }

    return (
      <div>
        <Navbar className="myNavbar">
          <Navbar.Brand>
            <img
              src={Logo}
              width="125"
              className="navbarBrand d-inline-block align-top"
              alt="React Bootstrap logo"
            />
          </Navbar.Brand>

          <div>{this.props.user.display_name}</div>
          <img className="profilePicture" src={this.props.user.images[0].url} />
          {this.checkAuthentication()}
        </Navbar>
      </div>
    );
  }
}

export default Header;
